			.root{
				width: 600px;
				height: 600px;
				background-color: pink;
				margin: 30px auto;
				/*开启3D效果 在父标签设置*/
				transform-style: preserve-3d;
				transform:rotateY(30deg) rotateX(30deg);
			}
