//		console.log("one被点击啦!!");
//	}
//鼠标右键  contextmenu
	one.addEventListener("contextmenu", function(){
		console.log("one被点击了!!!");
		//取消鼠标默认事件样式
		event.preventDefault();
	}, false);


	two.addEventListener("click", function(){
		console.log("two被点击了!!!");
		//阻止冒泡
//		event.cancelBubble = true;
