			var n1 = randomNum(0, 15);
			//16个元素,下标是0---15
			var n2 = randomNum(0, 15);
			if(n1 != n2){
				var t = arr[n1];
				arr[n1] = arr[n2];
				arr[n2] = t;
			}	
		}

	}

