<script type="text/javascript">
	document.write("<h1>break</h1>");
	for(var i = 1; i <= 50; i++){
		if(i == 20){
			document.write("有虫子"+"<br />");
			break;
		}
		document.write(i+"<br />");
	}
	document.write("<h1>continue</h1>");
	for(var i = 1; i <= 50; i++){
		if(i == 20){
			document.write("有虫子"+"<br />");
			continue;
		}
		document.write(i+" ");
	}
</script>
