	for(var i=1;i<=6;i++){
		for(var a=1;a<=6;a++){
			document.write(a+" ")
	    }
		document.write("<br />")
	}
	for(var i=1;i<=6;i++){
		for(var a=1;a<=i;a++){
			document.write(a+" ")
	    }
		document.write("<br />")
	}
	for(var i=6;i>=1;i--){
		for(var a=1;a<=i;a++){
			document.write(a+" ")
	    }
